import logo from './logo.svg';
import './App.css';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = [
			{
				name: A,
				id: 1
			},
			{
				name: B,
				id: 2
			},
			{
				name: C,
				id: 3
			}
		];
	}

	render() {
		return (
			<div className="App">
			{
				this.state.map(monster => {
					<div key={monster.id}>
						<h1>{monster.name}</h1>
					</div>
				})
			}
			</div>
		);
	}
}

export default App;
