// import logo from './logo.svg';
import './App.css';
import React from 'react';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.filterMonsters = this.filterMonsters.bind(this);
		this.state = {
			monsters: []
		};
		console.log('constructor');

	}

	componentDidMount() {
		console.log('componentDidMount');
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => this.setState(
			() => { return { monsters: users } }, 
			() => console.log('this.state:', this.state)
		));
	}

	filterMonsters(s) {
			const filteredMonsters = this.state.monsters.filter(monster => monster.name.toLowerCase().includes(s.toLowerCase()));
			this.setState(
				() => { return {monsters: filteredMonsters} },	
				() => console.log('this.state:', this.state)
			);
	}

	render() {
		console.log('render');
		return (
			<div className="App">
				<input 
					className='search-box' 
					type='input' 
					placeholder='search monsters'
					onChange={(event) => this.filterMonsters(event.target.value)}
				/>

				{
					this.state.monsters.map(monster => {
						return (
							<div key={monster.id}>
								<h1>{monster.name}</h1>
							</div>
						);
					})
				}
			</div>
		);
	}
}

export default App;
