import { useState, useEffect } from 'react';
import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component';
import './App.css';

const App = () => {
	const [searchField, setSearchField] = useState('');
	const [searchFieldClone, setSearchFieldClone] = useState('');
	const [monsters, setMonsters] = useState([]);

	useEffect(() => {
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => setMonsters(users));
	}, []);

	const onSearchChange = (event) => {
		const searchFieldString = event.target.value.toLocaleLowerCase();
		setSearchField(searchFieldString);
	}

	// console.log('searchField:', searchField);

	const onSearchChangeClone = (event) => {
		const searchFieldStringClone = event.target.value.toLocaleLowerCase();
		setSearchFieldClone(searchFieldStringClone);
		console.log('filteredMonsters:', filteredMonsters);
	}

	console.log('searchFieldClone:', searchFieldClone);

	const filteredMonsters = monsters.filter( 
		monster => monster.name.toLocaleLowerCase().includes(searchField)
	);

	return (
		<div className="App">
			<h1 className='app-title'>Monsters Rolodex</h1>
			<SearchBox
				className='monsters-search-box' 
				type='search' 
				placeholder='search monsters'
				onChangeHandler={onSearchChange}
			/> 
			<SearchBox
				className='monsters-search-box' 
				type='search' 
				placeholder='search someting...'
				onChangeHandler={onSearchChangeClone}
			/> 

			<CardList monsters={filteredMonsters} /> 
		</div>
	);
}

