import './App.css';
import React from 'react';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			monsters: [],
			filteredMonsters: []
		};
		console.log('constructor');

	}

	componentDidMount() {
		console.log('componentDidMount');
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => {
				this.setState(
					() => { return { monsters: users } }, 
					() => console.log('this.state:', this.state)
				);
				this.setState({ filteredMonsters: users })
		}
		);
	}

	render() {

		/* const filteredMonsters = this.state.monsters.filter(
			monster => monster.name.toLocaleLowerCase().includes(this.state.searchField.toLocaleLowerCase())
		); */

		console.log('render');
		return (
			<div className="App">
				<input 
					className='search-box' 
					type='input' 
					placeholder='search monsters'
					onChange={ event => {
							const searchField = event.target.value;
							// this.setState({ searchField });
							// console.log('this.state.monsters: ', this.state.monsters);
							/* const filteredMonsters = this.state.monsters.filter(
								monster => monster.name.toLocaleLowerCase().includes(this.state.searchField.toLocaleLowerCase())
							); */
							this.state.monsters.forEach(
								monster => console.log({ 'monster.name': monster.name })
							);
							this.setState({ filteredMonsters });
						}
					}
				/>

				{
					this.state.filteredMonsters.map(monster => {
						return (
							<div key={monster.id}>
								<h1>{monster.name}</h1>
							</div>
						);
					})
				}
			</div>
		);
	}
}

export default App;
