import { Component } from 'react';
import CardList from './components/card-list/card-list.component';
import './App.css';

class App extends Component {
	constructor(props) {
		super(props);
		this.onSearchChange = this.onSearchChange.bind(this);
		this.state = {
			monsters: [],
			filteredMonsters: []
		};
		console.log('constructor');
	}

	componentDidMount() {
		console.log('componentDidMount');
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => {
				this.setState(
					() => { return { monsters: users } }, 
					() => console.log('this.state:', this.state)
				);
				this.setState({ filteredMonsters: users })
			}
		);
	}

	onSearchChange(event) {
		const searchField = event.target.value.toLocaleLowerCase();
		const filteredMonsters = this.state.monsters.filter(
			monster => monster.name.toLocaleLowerCase().includes(searchField)
		);
		this.setState({ filteredMonsters });
	}

	render() {

		const { filteredMonsters } = this.state;
		const { onSearchChange } = this;

		console.log('render');
		return (

			<div className="App">

				<input 
					className='search-box' 
					type='input' 
					placeholder='search monsters'
					onChange={onSearchChange}
				/>

				{/* {
					filteredMonsters.map(monster => {
						return (
							<div key={monster.id}>
								<h1>{monster.name}</h1>
							</div>
						);
					})
				} */}
				<CardList />

			</div>

		);
	}
}

export default App;
